mapper.select:


     and cl.id = #{id}
     and cl.user_id = #{userId}
     and cl.mobile = #{mobile}
     and cl.name like concat('%', #{name}, '%')
     and cl.superior_id = #{superiorId}
     and cl.superior_code = #{superiorCode}
     and cl.superior_name like concat('%', #{superiorName}, '%')
     and cl.content = #{content}
     and cl.before_money = #{beforeMoney}
     and cl.later_money = #{laterMoney}
     and cl.money = #{money}
     and cl.type = #{type}
     and cl.direct = #{direct}
     and cl.create_time = #{createTime}
     and cl.update_time = #{updateTime}
     and cl.order_id = #{orderId}
     and cl.stock_id = #{stockId}
     and cl.stock_name = #{stockName}
     and cl.stock_symbol = #{stockSymbol}
     and cl.delete_flag = #{deleteFlag}

        AND date_format(cl.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        AND date_format(cl.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

     and m.weiyima like concat('%', #{weiyima}, '%')
     and m.salt like concat('%', #{salt}, '%')

and cl.user_id in (
    SELECT
        m.id
    FROM
        fa_member m
    WHERE
        m.daili_id IN ( SELECT #{parentId} UNION ALL SELECT u.user_id FROM sys_user u WHERE u.path LIKE concat('%,', #{parentId}, ',%') )
)
order by id desc

mapper.update:
update fa_capital_log
user_id = #{userId}, mobile = #{mobile}, name = #{name}, superior_id = #{superiorId}, superior_code = #{superiorCode}, superior_name = #{superiorName}, content = #{content}, before_money = #{beforeMoney}, later_money = #{laterMoney}, money = #{money}, type = #{type}, direct = #{direct}, create_time = #{createTime}, update_time = #{updateTime}, order_id = #{orderId}, stock_id = #{stockId}, stock_name = #{stockName}, stock_symbol = #{stockSymbol}, delete_flag = #{deleteFlag},
where id = #{id}
