mapper.select:


     and cl.id = #{id}
     and cl.template_id = #{templateId}
     and cl.user_id = #{userId}
     and cl.name like concat('%', #{name}, '%')
     and cl.id_card = #{idCard}
     and cl.address = #{address}
     and cl.main_content = #{mainContent}
     and cl.image = #{image}
     and cl.create_time = #{createTime}
     and cl.update_time = #{updateTime}
     and cl.delete_time = #{deleteTime}
     and cl.weigh = #{weigh}
     and cl.sign_status = #{signStatus}
     and cl.sign_date = #{signDate}
     and cl.delete_flag = #{deleteFlag}
     and m.weiyima like concat('%', #{weiyima}, '%')
     and m.salt like concat('%', #{salt}, '%')

order by cl.id desc

mapper.update:
update fa_contract_list
template_id = #{templateId}, user_id = #{userId}, name = #{name}, id_card = #{idCard}, address = #{address}, main_content = #{mainContent}, image = #{image}, create_time = #{createTime}, update_time = #{updateTime}, delete_time = #{deleteTime}, weigh = #{weigh}, sign_status = #{signStatus}, sign_date = #{signDate}, delete_flag = #{deleteFlag},
where id = #{id}
