mapper.select:


     and sss.id = #{id}
     and sss.user_id = #{userId}
     and sss.shengou_id = #{shengouId}
     and sss.code = #{code}
     and sss.all_code = #{allCode}
     and sss.name like concat('%', #{name}, '%')
     and sss.sg_fx_price = #{sgFxPrice}
     and sss.sg_hy_rate = #{sgHyRate}
     and sss.sg_num = #{sgNum}
     and sss.sg_nums = #{sgNums}
     and sss.max_sg = #{maxSg}
     and sss.status = #{status}
     and sss.money = #{money}
     and sss.create_time = #{createTime}
     and sss.update_time = #{updateTime}
     and sss.dj_money = #{djMoney}
     and sss.sg_sg_date = #{sgSgDate}
     and sss.sg_zq_jk_date = #{sgZqJkDate}
     and sss.sg_ss_date = #{sgSsDate}
     and sss.renjiao = #{renjiao}
     and sss.renjiao_time = #{renjiaoTime}
     and sss.is_cc = #{isCc}
     and sss.is_cc_time = #{isCcTime}
     and sss.zq_money = #{zqMoney}
     and sss.zq_num = #{zqNum}
     and sss.zq_nums = #{zqNums}
     and sss.freeze_profit = #{freezeProfit}
     and sss.balance = #{balance}
     and sss.old_sg_num = #{oldSgNum}
     and sss.old_sg_nums = #{oldSgNums}
     and sss.sg_type = #{sgType}
     and sss.delete_flag = #{deleteFlag}

     and m.name like concat('%', #{memberName}, '%')
     and m.mobile like concat('%', #{mobile}, '%')
     and m.weiyima like concat('%', #{weiyima}, '%')
     and m.salt like concat('%', #{salt}, '%')

and sss.user_id in (
SELECT
m.id
FROM
fa_member m
WHERE
m.daili_id IN ( SELECT #{parentId} UNION ALL SELECT u.user_id FROM sys_user u WHERE u.path LIKE concat('%,', #{parentId}, ',%') )
)
order by sss.id desc

mapper.update:
update fa_stock_sg_second
user_id = #{userId}, shengou_id = #{shengouId}, code = #{code}, all_code = #{allCode}, name = #{name}, sg_fx_price = #{sgFxPrice}, sg_hy_rate = #{sgHyRate}, sg_num = #{sgNum}, sg_nums = #{sgNums}, max_sg = #{maxSg}, status = #{status}, money = #{money}, create_time = #{createTime}, update_time = #{updateTime}, dj_money = #{djMoney}, sg_sg_date = #{sgSgDate}, sg_zq_jk_date = #{sgZqJkDate}, sg_ss_date = #{sgSsDate}, renjiao = #{renjiao}, renjiao_time = #{renjiaoTime}, is_cc = #{isCc}, is_cc_time = #{isCcTime}, zq_money = #{zqMoney}, zq_num = #{zqNum}, zq_nums = #{zqNums}, freeze_profit = #{freezeProfit}, balance = #{balance}, old_sg_num = #{oldSgNum}, old_sg_nums = #{oldSgNums}, sg_type = #{sgType}, delete_flag = #{deleteFlag},
where id = #{id}
